// Initialize library with data output pin, clock input pin and gain factor.\n  // Channel selection is made by passing the appropriate gain:\n  // - With a gain factor of 64 or 128, channel A is selected\n  // - With a gain factor of 32, channel B is selected\n  // By omitting the gain factor parameter, the library\n  // default "128" (Channel A) is used here.\n  \n  scale.begin(LOADCELL_DOUT_PIN, LOADCELL_SCK_PIN);\n\n  Serial.println("Before setting up the scale:");\n    if (scale.wait_ready_timeout(1000)) {\n    long reading = scale.read();\n    Serial.print("HX711 reading: ");\n    Serial.println(reading);\n  } else {\n    Serial.println("HX711 not found.");\n  }   // print a raw reading from the ADC\n\n  Serial.print("read average: \\t\\t");\n  Serial.println(scale.read_average(20));   // print the average of 20 readings from the ADC\n\n  Serial.print("get value: \\t\\t");\n  Serial.println(scale.get_value(5));       // print the average of 5 readings from the ADC minus the tare weight (not set yet)\n\n  Serial.print("get units: \\t\\t");\n  Serial.println(scale.get_units(5), 1);    // print the average of 5 readings from the ADC minus tare weight (not set) divided\n                        // by the SCALE parameter (not set yet)\n\n  scale.set_scale(2280.f);                      // this value is obtained by calibrating the scale with known weights; see the README for details\n  scale.tare();                     // reset the scale to 0\n\n  Serial.println("After setting up the scale:");\n\n  Serial.print("read: \\t\\t");\n  Serial.println(scale.read());                 // print a raw reading from the ADC\n\n  Serial.print("read average: \\t\\t");\n  Serial.println(scale.read_average(20));       // print the average of 20 readings from the ADC\n\n  Serial.print("get value: \\t\\t");\n  Serial.println(scale.get_value(5));       // print the average of 5 readings from the ADC minus the tare weight, set with tare()\n\n  Serial.print("get units: \\t\\t");\n  Serial.println(scale.get_units(5), 1);        // print the average of 5 readings from the ADC minus tare weight, divided\n                        // by the SCALE parameter set with set_scale\n\n  Serial.println("Readings:");\n}\n\nvoid loop() {\n  Serial.print("one reading:\\t");\n  Serial.print(scale.get_units(), 1);\n  Serial.print("\\t| average:\\t");\n  Serial.println(scale.get_units(10), 1);\n\n  scale.power_down();                   // put the ADC in sleep mode\n  delay(5000);\n  scale.power_up();\n}\n\n')),(0,a.yg)("p",null,(0,a.yg)("strong",{parentName:"p"},"Step 2."),'  Please click the Board selection dropdown menu, and then click on "Select other board and port...".'),(0,a.yg)("div",{style:{textAlign:"center"}},(0,a.yg)("img",{src:"https://files.seeedstudio.com/wiki/Grove-ADC-for-Load-Cell-HX711-/img/4.png",style:{width:400,height:"auto"}})),(0,a.yg)("p",null,(0,a.yg)("strong",{parentName:"p"},"Step 3."),' And then search and select "Arduino UNO R4 WiFi". '),(0,a.yg)("div",{style:{textAlign:"center"}},(0,a.yg)("img",{src:"https://files.seeedstudio.com/wiki/Grove-ADC-for-Load-Cell-HX711-/img/5.png",style:{width:600,height:"auto"}})),(0,a.yg)("p",null,(0,a.yg)("strong",{parentName:"p"},"Step 4."),' Please click "upload" to flash the firmware on to Arduino.'),(0,a.yg)("div",{style:{textAlign:"center"}},(0,a.yg)("img",{src:"https://files.seeedstudio.com/wiki/Grove-ADC-for-Load-Cell-HX711-/img/6.png",style:{width:400,height:"auto"}})),(0,a.yg)("p",null,(0,a.yg)("strong",{parentName:"p"},"Step 5."),"  Open the ",(0,a.yg)("strong",{parentName:"p"},"Serial Monitor")," of Arduino IDE by click ",(0,a.yg)("strong",{parentName:"p"},"Tool-> Serial Monitor"),'. When you see serial print "After setting up the scale:", wait a few seconds and apply force to ',(0,a.yg)("strong",{parentName:"p"},"Weight Sensor (Load Cell) 0-500g"),"."),(0,a.yg)("div",{style:{textAlign:"center"}},(0,a.yg)("img",{src:"https://files.seeedstudio.com/wiki/Grove-ADC-for-Load-Cell-HX711-/img/10.gif",styl