5, 0x06, 0x07, 0x08, 0x09, 0x0a, 0x0b, 0x0c, 0x0d, // Key 0\n  0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, // Key 1\n  0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, // Key 2\n  0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00  // Key 3\n};\n\n// setup the wireless mode\n// infrastructure - connect to AP\n// adhoc - connect to another WiFi device\nunsigned char wireless_mode = WIRELESS_MODE_INFRA;\n\nunsigned char ssid_len;\nunsigned char security_passphrase_len;\n// End of wireless configuration parameters ----------------------------------------\n\n\n// Function that prints data from the server\nvoid printData(char* data, int len) {\n\n  // Print the data returned by the server\n  // Note that the data is not null-terminated, may be broken up into smaller packets, and\n  // includes the HTTP header.\n  while (len-- > 0) {\n    Serial.print(*(data++));\n  }\n}\n\nint sensorValue=0;\n//Prepare data to the feed\nvoid pachubefeedData()\n{\n   sensorValue=analogRead(A2);   //Read analog sensor connected to A2 (Use Grove - XBee Carrier interface)\n   WiServer.print(sensorValue);\n}\n\n\n\n// IP Address for pachube.com\nuint8 ip[] = {173,203,98,29};\n\nchar hostName[] = "www.pachube.com\\nX-PachubeApiKey: YOUR_API_KEY_HERE\\nConnection: close"; // Replace YOUR_API_KEY_HERE , with your API key\nchar url[] = "/api/12345.csv?_method=put"; //Replace 12345 with your feed number\n\n// A request that POSTS data to Pachube\nPOSTrequest postPachubeFeed(ip, 80, hostName, url, pachubefeedData);\n\n\nvoid setup() {\n    // Initialize WiServer (we\'ll pass NULL for the page serving function since we don\'t need to serve web pages)\n  WiServer.init(NULL);\n\n  // Enable Serial output and ask WiServer to generate log messages (optional)\n  Serial.begin(57600);\n  WiServer.enableVerboseMode(true);\n\n  // Have the processData function called when data is returned by the server\n  postPachubeFeed.setReturnFunc(printData);\n}\n\n\n// Time (in millis) when the data should be retrieved\nlong updateTime = 0;\n\nvoid loop(){\n\n  // Check if it\'s time to send an update\n  if (millis() >= updateTime) {\n    postPachubeFeed.submit();    \n    // Send another update after a min\n    updateTime += 60;\n  }\n\n  // Run WiServer\n  WiServer.server_task();\n\n  delay(10);\n}\n')),(0,r.yg)("h2",{id:"bill-of-materials-bom-parts-list"},"Bill of Materials (BOM) /parts list"),(0,r.yg)("table",null,(0,r.yg)("thead",{parentName:"table"},(0,r.yg)("tr",{parentName:"thead"},(0,r.yg)("th",{parentName:"tr",align:null},"Part"),(0,r.yg)("th",{parentName:"tr",align:null},"Quantity"),(0,r.yg)("th",{parentName:"tr",align:null},"Value"),(0,r.yg)("th",{parentName:"tr",align:null},"Package"))),(0,r.yg)("tbody",{parentName:"table"},(0,r.yg)("tr",{parentName:"tbody"},(0,r.yg)("td",{parentName:"tr",align:null},"C1"),(0,r.yg)("td",{parentName:"tr",align:null},"1"),(0,r.yg)("td",{parentName:"tr",align:null},"10u"),(0,r.yg)("td",{parentName:"tr",align:null},"C_TAN_3X3.5")),(0,r.yg)("tr",{parentName:"tbody"},(0,r.yg)("td",{parentName:"tr",align:null},"C2,C3"),(0,r.yg)("td",{parentName:"tr",align:null},"2"),(0,r.yg)("td",{parentName:"tr",align:null},"1u"),(0,r.yg)("td",{parentName:"tr",align:null},"0603")),(0,r.yg)("tr",{parentName:"tbody"},(0,r.yg)("td",{parentName:"tr",align:null},"C4"),(0,r.yg)("td",{parentName:"tr",align:null},"1"),(0,r.yg)("td",{parentName:"tr",align:null},"100n"),(0,r.yg)("td",{parentName:"tr",align:null},"0603")),(0,r.yg)("tr",{parentName:"tbody"},(0,r.yg)("td",{parentName:"tr",align:null},"J1"),(0,r.yg)("td",{parentName:"tr",align:null},"1"),(0,r.yg)("td",{parentName:"tr",align:null},"CK_2X3_2.0"),(0,r.yg)("td",{parentName:"tr",align:null},"CK_2X3_2.0")),(0,r.yg)("tr",{parentName:"tbody"},(0,r.yg)("td",{parentName:"tr",align:null},"PWR"),(0,r.yg)("td",{parentName:"tr",align:null},"1"),(0,r.yg)("td",{parentName:"tr",align:null},"red"),(0,r.yg)("td",{parentName:"tr",align: